import React, { Component, Suspense } from 'react';
import { Link } from "react-router-dom";
import Slider from "react-slick";

// Import css files
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import Banner1 from '../../Assets/Images/mobile-home-banner-1.png';
import Banner2 from '../../Assets/Images/mobile-home-banner-2.png';
import Banner3 from '../../Assets/Images/mobile-home-banner-3.png';

/* eslint-disable */
class MobileHomeHeader extends Component {
    render() {
        const settings = {
            arrows: false,
            slidesToShow: 1,
            centerPadding: 0,
        };
        const HomePageBanner = [ { src: Banner1 }, { src: Banner2 }, { src: Banner3 } ];
        return (
            <section id="mobile-home-banner">
                <Suspense fallback={<div>Loading...</div>}>
                    <Slider {...settings}>
                        {HomePageBanner.map((banner, i) => (
                            <div key={'home-page-banner'+i}>
                                <img src={banner.src} alt={'Home page banner'} />
                            </div>
                        ))}
                    </Slider>
                </Suspense>
                <div className="home-banner-button-mobile mt-3">
                    <Link to="/estimate" className="btn red-btn">Get A free quote</Link>
                </div>
                {/* <div className="banner-content d-flex align-items-center"> */}
                    <div className="mobile-banner-content">
                        <div className="left">
                            <h1>Excellence in Design, Quality Workmanship and a Professional Experience</h1>
                            <span className="sub-head">Serving the Greater Toronto Area for over 20 years</span>
                        </div>
                    </div>
                {/* </div> */}
            </section>
        );
    }
}

export default MobileHomeHeader;
